    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        String paciente = txtPaciente.getText();
        String dentista = txtDentista.getText();
        String data = txtData.getText();
        String hora = txtHora.getText();
        String tratamento = txtTratamento.getText();

        int id = TelaAgenda.gerarId();

        Agenda c = new Agenda(id, paciente, dentista, data, hora, tratamento);

        telaAgenda.adicionarConsultaNaTabela(c);

        TelaAgenda.getConsultas().add(c);

        JOptionPane.showMessageDialog(this, "Consulta cadastrada com sucesso!");

        dispose();
    }//GEN-LAST:event_btnCadastrarActionPerformed

